package hva.nl.studentportalimport android.annotation.SuppressLintimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport kotlinx.android.synthetic.main.student_portal_item.view.*class PortalAdapter(private val portals: ArrayList<Portal>, private val clickListener: (Portal) -> Unit) :    RecyclerView.Adapter<RecyclerView.ViewHolder>() {    override fun onBindViewHolder(holder: RecyclerView.ViewHolder, position: Int) {        (holder as ViewHolder).bind(portals[position], clickListener)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        return ViewHolder(LayoutInflater.from(parent.context).inflate(R.layout.student_portal_item, parent, false))    }    override fun getItemCount(): Int {        return portals.size    }    inner class ViewHolder(itemview: View) : RecyclerView.ViewHolder(itemview) {        @SuppressLint("SetTextI18n")        fun bind(portal: Portal, clickListener: (Portal) -> Unit) {            itemView.tvPortalTitle.text = portal.title + "\n" + portal.uri            itemView.tvPortalHiddenButton.setOnClickListener{clickListener(portal)}        }    }}